mapper.update:
update user
set password = #{password}
where username = #{username}

mapper.select:
select u.* , r.role_type from user u left join role r on u.type_key = r.type_key where 1=1
and u.username like concat('%' , #{param.username} , '%')
and r.role_type = #{param.roleType}
and u.real_name like concat('%' , #{param.realName} , '%')
and u.education = #{param.education}

select a.status,b.* from application a RIGHT JOIN (select u.real_name,u.sex,u.idCard,u.birth,u.nature,u.hometown,u.education, u.college,u.mobilePhone,u.email,u.address,u.username, s.applied_position,s.english_skill,s.computer_skill,s.main_skill from user u left join skill s on u.real_name = s.real_name where type_key = 0) b on a.username = b.username where 1=1
and s.applied_position = #{param.appliedPosition}
and b.real_name = #{param.realName}
and b.education = #{param.education}
